package org.ip.sesion03;

import java.util.Scanner;

public class TrianguloRectanguloAsteriscos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner entrada = new Scanner(System.in);
		int ladoTriangulo = 0;

		do {
			System.out.print("Triangulo rectangulo de asteriscos de lado = ");
			ladoTriangulo = entrada.nextInt();
		} while (ladoTriangulo < 3 || ladoTriangulo > 15);

		System.out.println();

		for (int i = 0; i < ladoTriangulo; i++) {
			for (int j = 0; j < (ladoTriangulo - i); j++) {
				System.out.print("* ");
			}
			System.out.println();
		}

	}

}
